(DATE="$(date +"%d/%b/%Y")";BUSYSITE="$(grep -s "$DATE" /usr/local/apache/logs/domlogs/*|grep GET|awk '{print $1}'|cut -d':' -f1|sort |uniq -c|sort -n|tail -n1|awk '{print $2}')";echo "";echo "Checking $BUSYSITE traffic on $DATE:";echo "";echo "URL's with the most POST requests:";grep -s "$DATE" $BUSYSITE|grep POST|awk '{print $7}'|sort |uniq -c|sort -n|tail -n5;echo "";echo "IP's making those POST requests:";grep -s "$DATE" $BUSYSITE|grep POST|awk '{print $1}'|sort |uniq -c|sort -n|tail -n5;echo "";echo "URL's with the most GET requests:";grep -s "$DATE" $BUSYSITE|grep GET|awk '{print $7}'|sort |uniq -c|sort -n|tail -n5;echo "";echo "IP's making those GET requests:";grep -s "$DATE" $BUSYSITE|grep GET|awk '{print $1}'|sort |uniq -c|sort -n|tail -n5;echo "")
